package p200917;

public class P14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//산술 연산자 + - * / %
		int v1 = 10/3; //정수 나누기...
		double v2 = 10/3.0; //실수 나누기... 실수 나누기를 할려면 double
		int v3 = 10%3; //10을 3으로 나눈 나머지
		String v4 = "안녕하세요" + 113 + 7 + "반갑습니다."; // 문자열 연산...
		int v5 = 10; 
		
		//대입연산자... 수행 될때마다 메모리값 바뀜...
		v5 += 5;
		v5 -= 10;
		
		//증감 연산자... 1씩 +... 1씩 -
		v5++; 
		v5--;
		
		//비교, 논리... 다 중요...!!!!
		//비교 : == != < > >= <=
		//논리 연산자 && || !
		
		
		
	}

}
