public class p4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		if(true) {
			//....
		}
		//예외처리인 경우 많이 사용
		
		if(true) {
			//...
		}else { 
			//...
		} // if 아니면 else
		
		int value = 0;
		
		if(value ==1) {
			//...
		}else if(value ==2) {
			//...
		}else if(value ==3) {
			//...
		}else {
			//...
		} 
		//if가 true면 밑에 조건 건너서 바로 결과 도출, else if도 true면 밑에 조건 건너서 바로 결과 도출의 반복
		
		
		
		
	}

}
